(1, 'Samia Allouba Team', 'sdf', 'sdfsdf', 'sdf', '', 'sdf', NULL, './images/team/643fa703c9c73a6cf3500e0f98ec534f.jpg', './images/team/thumb/resize_643fa703c9c73a6cf3500e0f98ec534f.jpg', 0, 0, 201, '2018-11-05 13:58:01', '2018-11-14 02:33:58'),
(5, 'Aly Mazhar', '', '', 'ISSA Sports & Conditioning Instructor  CrossFit Certified L1 & L2 Trainer  CrossFit Weightlifting Trainer Course  SHP Compound Exercise Analysis Course  SHP Program Design Course   OPEX Movem', '', 'Founder & Head Coach', '<p><em>Mazhar comes in with an extensive athletic background as an Ex-professional Football player who has represented the Egyptian National Team since he was 17.</em></p>\r\n\r\n<p><em>He graduated from college to work as an Investment Banker at EFG Hermes and later as a Financial Analyst at GE.</em></p>\r\n\r\n<p><em>In 2013, Aly Mazhar chose to take a totally different route through which he could serve his passion for Sports and Fitness. He founded BeFit in June 2013.</em></p>\r\n\r\n<p><em>He is currently one of Egypt&rsquo;s finest athletes representing global brands and acting as Nike Running Ambassador, KT Tape Ambassador, and Polar Fitness Ambassador.</em></p>\r\n', './images/team/Aly Mazhar.jpg', './images/team/thumb/962259_resized827375.jpg', 0, 0, 95, '2019-01-01 09:32:23', NULL),
(7, 'AHMED SHAMA', '', '', 'ISSA Strength and Conditioning Instructor Boxercise Instructor  CrossFit Certified L1 Trainer  SHP Compound Exercise Analysis Course  SHP Program Design Course   OPEX Movement and Assessment ', '', 'Partner and head Coach', '<p><em>A lifetime sports enthusiast who grew up with an interest in a number of sports including tennis, basketball, football and boxing. Shama opened the first mixed martial arts gym in Cairo in 2000 and got certified as a Boxercise Instructor.</em></p>\r\n\r\n<p><em>After attending a BeFit session in October 2013, Shama got hooked and hasn&#39;t stopped working out since. With a lot of hard work, dedication, and persistence, Shama became one of BeFit&rsquo;s finest athletes and started his coaching career.&nbsp;</em></p>\r\n', './images/team/Ahmed .jpg', './images/team/thumb/33800_resized692079.jpg', 0, 0, 95, '2019-01-02 06:35:29', NULL);
